package cellsociety_team11;

public class SimulationInstantiationException extends RuntimeException{

	private static final long serialVersionUID = 1L;

	public SimulationInstantiationException(String message){
		// TODO Auto-generated constructor stub
		super(message);
	}
	
	public SimulationInstantiationException(Throwable cause){
		super(cause);
	}
	
	public SimulationInstantiationException(String message, Throwable cause){
		super(message, cause);
	}

}
